import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";

// Components
import { Footer } from "../Components/Footer";
import { Header } from "../Components/Header";
import { Form } from "../Components/Sign Up/Form";

// Interface

// Sign Up Form data
interface SignUpFormData {
  title: string;
  description: string;
  formTag1: string;
  formTag2: string;
  formTag3: string;
}

const tempData = {
  business: {
    form: {
      title: "Business Sign Up",
      description:
        "Unlock the full potential of our business resources and support. As a member, you'll have access to various resources. With our help, you can take your business to the next level and achieve success!",
      formTag1: "Business Name",
      formTag2: "Owner Full Name",
      formTag3: "Business Description",
    },
    roles: {
      title: "Business Roles",
      description: "Select the roles that best describe your business:",
      formTag1: "Manufacturer",
      formTag2: "Distributor",
      formTag3: "Retailer",
    },
  },
  mentor: {
    form: {
      title: "Mentor Sign Up",
      description:
        "Join our community of experienced professionals and share your knowledge and expertise with others. As a mentor, you will have the opportunity to guide and support individuals on their professional journey.",
      formTag1: "Full Name",
      formTag2: "Profession",
      formTag3: "Description",
    },
    roles: {
      title: "Mentor Roles",
      description:
        "Select the roles that best describe your mentoring expertise:",
      formTag1: "Career Development",
      formTag2: "Entrepreneurship",
      formTag3: "Leadership",
    },
  },
  hr: {
    form: {
      title: "Human Resource Sign Up",
      description:
        "Join our team of human resources experts and help shape the future of our company. As an HR representative, you will play a crucial role in building and maintaining a positive and productive work environment.",
      formTag1: "Full Name",
      formTag2: "Employee ID",
      formTag3: "Description",
    },
    roles: {
      title: "Job Role",
      description:
        "Join our team of human resources experts and help shape the future of our company. As an HR representative, you will play a crucial role in building and maintaining a positive and productive work environment.",
      formTag1: "Full Name",
      formTag2: "Employee ID",
      formTag3: "Description",
    },
  },
};

export const SignUp = () => {
  const [signUpForm, setSignUpForm] = useState<SignUpFormData>({
    title: "",
    description: "",
    formTag1: "",
    formTag2: "",
    formTag3: "",
  });

  const parameters: string | undefined = useParams().user;

  useEffect(() => {
    if (parameters === "business") {
      setSignUpForm(tempData.business.form);
    } else if (parameters === "mentor") {
      setSignUpForm(tempData.mentor.form);
    } else if (parameters === "hr") {
      setSignUpForm(tempData.hr.form);
    } else {
    }
  }, [parameters]);

  return (
    <section>
      <Header />
      <main>
        <section className="form-section">
          <form>
            {/* Top Section */}
            <Form {...signUpFormType} />

            {/* Middle Section */}

            {/* Bottom Section */}
            <p className="sign-up-title">Sign Up Information</p>
            <p className="sign-up-description"></p>
            <input className="form-input" placeholder="Email Address"></input>
            <input className="form-input" placeholder="Phone Number"></input>
            <input className="form-input" placeholder="Password"></input>
            <input
              className="form-input"
              placeholder="Confirm Password"
            ></input>

            <button>Sign Up</button>
          </form>
        </section>
        <section className="icon-section"></section>
      </main>
      <Footer />
    </section>
  );
};
