import { useState } from "react";

interface Roles {
  title: string;
  description: string;
  role1: string;
  role2: string;
  role3: string;
  role4: string;
  role5: string;
  role6: string;
}

interface Props {
  rolesData: Roles;
  setRoles: string[];
}

export const InterestsRoles = ({ rolesData, roles }: Props) => {
  const [checkedRoles, setCheckedRoles] = useState<string[]>([]);

  const handleCheckboxChange = (role: string) => {
    if (checkedRoles.includes(role)) {
      setCheckedRoles(checkedRoles.filter((r) => r !== role));
    } else {
      setCheckedRoles([...checkedRoles, role]);
    }
    setRoles(checkedRoles);
  };

  return (
    <>
      <p className="sign-up-title">{rolesData.title}</p>
      <p className="sign-up-description">{rolesData.description}</p>

      {/* Roles */}
      <input
        className="form-input"
        type="checkbox"
        name="role1"
        onChange={() => handleCheckboxChange(rolesData.role1)}
      />
      <label htmlFor="role1">{rolesData.role1}</label>

      <input
        className="form-input"
        type="checkbox"
        name="role2"
        onChange={() => handleCheckboxChange(rolesData.role2)}
      />
      <label htmlFor="role2">{rolesData.role2}</label>

      <input
        className="form-input"
        type="checkbox"
        name="role3"
        onChange={() => handleCheckboxChange(rolesData.role3)}
      />
      <label htmlFor="role3">{rolesData.role3}</label>

      <input
        className="form-input"
        type="checkbox"
        name="role4"
        onChange={() => handleCheckboxChange(rolesData.role4)}
      />
      <label htmlFor="role4">{rolesData.role4}</label>

      <input
        className="form-input"
        type="checkbox"
        name="role5"
        onChange={() => handleCheckboxChange(rolesData.role5)}
      />
      <label htmlFor="role5">{rolesData.role5}</label>

      <input
        className="form-input"
        type="checkbox"
        name="role6"
        onChange={() => handleCheckboxChange(rolesData.role6)}
      />
      <label htmlFor="role6">{rolesData.role6}</label>
    </>
  );
};
