import React, { useState } from "react";

interface Roles {
  title: string;
  description: string;
  role1: string;
  role2: string;
  role3: string;
  role4: string;
  role5: string;
  role6: string;
}

interface Props {
  rolesData: Roles;
  setRoles: (roles: string[]) => void;
}

export const InterestsRoles = ({ rolesData, setRoles }: Props) => {
  const [selectedRoles, setSelectedRoles] = useState<string[]>([]);

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const role = event.target.name;
    const checked = event.target.checked;
    if (checked) {
      setSelectedRoles([...selectedRoles, role]);
    } else {
      setSelectedRoles(
        selectedRoles.filter((selectedRole) => selectedRole !== role)
      );
    }
    setRoles(selectedRoles);
  };

  return (
    <>
      <p className="sign-up-title">{rolesData.title}</p>
      <p className="sign-up-description">{rolesData.description}</p>
      {Object.values(rolesData).map((value, index) => {
        if (index === 0 || index === 1) {
          return null;
        }
        return (
          <>
            <input
              className="form-input"
              type="checkbox"
              name={value}
              onChange={handleChange}
            />
            <label htmlFor={value}>{value}</label>
          </>
        );
      })}
    </>
  );
};
