import '../Styles/Header.css';
import { useState } from 'react'

export default function Header(){

    const [ menuClass, setMenuClass ] = useState('')
    const [ listClass, setListClass ] = useState('')

    const menuClickHandler = () => {
        if (menuClass === '') {
            setMenuClass('change')
            setListClass('')
        } else {
            setMenuClass('hidden')
        }
    }

    return (
        <header className='Header'>

            <img src={require('../Images/Header/BOFA-logo-colored.png')} alt='bank of america logo' id='logo' />

            <nav>
                <div id="container" className={menuClass} onClick={menuClickHandler}>
                        <div className="bar1"></div>
                        <div className="bar2"></div>
                        <div className="bar3"></div>
                </div>

                <ul className={listClass}>
                    <a href='#'><li>Resources</li></a>
                    <a href='#'><li>Mentor</li></a>
                    <a href='#'><li>About Us</li></a>
                </ul>

                <section className='icon-container'>
                    <img src={require('../Images/Header/search-icon.png')} alt='search icon' />
                    <img src={require('../Images/Header/profile-icon.png')} alt='profile icon'/>
                </section>
            </nav>

        </header>

    );
}
