import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";

// Components
import { Footer } from "../Components/Footer";
import { Header } from "../Components/Header";
import { Form } from "../Components/Sign Up/Form";
import { InterestsRoles } from "../Components/Sign Up/InterestsRoles";

// CSS
import "../Styles/SignUp.css";

// Interface

// Sign Up Form data
interface FormSignUp {
  title: string;
  description: string;
  formTag1: string;
  formTag2: string;
  formTag3: string;
}

// Sign Up Roles
interface Roles {
  title: string;
  description: string;
  role1: string;
  role2: string;
  role3: string;
  role4: string;
  role5: string;
  role6: string;
}

interface SignUpI {
  form: FormSignUp;
  roles: Roles;
}

export const SignUp = () => {
  const [signUpForm, setSignUpForm] = useState<SignUpI>({
    form: {
      title: "",
      description: "",
      formTag1: "",
      formTag2: "",
      formTag3: "",
    },
    roles: {
      title: "",
      description: "",
      role1: "",
      role2: "",
      role3: "",
      role4: "",
      role5: "",
      role6: "",
    },
  });

  const [roles, setRoles] = useState([]);

  const parameters: string | undefined = useParams().user;

  const handleSubmit = (e: any) => {
    e.preventDefault();
    const formData = new FormData(e.target.form);
    const data = Object.fromEntries(formData.entries());
    console.log({ ...data, roles: roles });
  };

  useEffect(() => {
    if (parameters === "business") {
      setSignUpForm(tempData.business);
    } else if (parameters === "mentor") {
      setSignUpForm(tempData.mentor);
    } else if (parameters === "hr") {
      setSignUpForm(tempData.hr);
    } else {
    }
  }, [parameters]);

  return (
    <section>
      <Header />
      <main>
        <section className="form-section">
          <form onSubmit={handleSubmit}>
            {/* Top Section */}
            <Form {...signUpForm.form} />

            <hr />

            {/* Middle Section */}
            <InterestsRoles {...signUpForm.roles} />

            <hr />

            {/* Bottom Section */}
            <p className="sign-up-title">Sign Up Information</p>
            <p className="sign-up-description"></p>
            <input
              className="form-input"
              placeholder="Email Address"
              name="email"
            ></input>
            <input
              className="form-input"
              placeholder="Phone Number"
              name="phone"
            ></input>
            <input
              className="form-input"
              placeholder="Password"
              name="password"
            ></input>
            <input
              className="form-input"
              placeholder="Confirm Password"
              name="confirmPassword"
            ></input>

            <input className="btn" type="submit" value="Sign Up" onClick={handleSubmit}></input>
          </form>
          <p className="redirect-sign-up">
            Not a Mentor or a Business? Sign up as an Employee
          </p>
        </section>
        <section className="icon-section">
          <img></img>
        </section>
      </main>
      <Footer />
    </section>
  );
};

const tempData = {
