import React, { useState } from "react";

interface Roles {
  title: string;
  description: string;
  role1: string;
  role2: string;
  role3: string;
  role4: string;
  role5: string;
  role6: string;
}

interface Props {
  rolesData: Roles;
  setRoles: (roles: string[]) => void;
}

export const InterestsRoles = ({ rolesData, setRoles }: Props) => {
  console.log(rolesData);
  const [roles, setCheckedRoles] = useState<string[]>([]);

  const handleCheckboxChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const role = rolesData[event.target.name];
    if (event.target.checked) {
      setCheckedRoles([...roles, role]);
    } else {
      setCheckedRoles(roles.filter((r) => r !== role));
    }
    setRoles(roles);
  };

  return (
    <>
      <p className="sign-up-title">{rolesData.title}</p>
      <p className="sign-up-description">{rolesData.description}</p>

      {/* Roles */}
      <input
        className="form-input"
        type="checkbox"
        name="role1"
        onChange={handleCheckboxChange}
      ></input>
      <label htmlFor="role1">{rolesData.role1}</label>

      <input
        className="form-input"
        type="checkbox"
        name="role2"
        onChange={handleCheckboxChange}
      ></input>
      <label htmlFor="role2">{rolesData.role2}</label>

      <input
        className="form-input"
        type="checkbox"
        name="role3"
        onChange={handleCheckboxChange}
      ></input>
      <label htmlFor="role3">{rolesData.role3}</label>

      <input
        className="form-input"
        type="checkbox"
        name="role4"
        onChange={handleCheckboxChange}
      ></input>
      <label htmlFor="role4">{rolesData.role4}</label>

      <input
        className="form-input"
        type="checkbox"
        name="role5"
        onChange={handleCheckboxChange}
      ></input>
      <label htmlFor="role5">{rolesData.role5}</label>

      <input
        className="form-input"
        type="checkbox"
        name="role6"
        onChange={handleCheckboxChange}
      ></input>
      <label htmlFor="role6">{rolesData.role6}</label>
    </>
  );
};
